mapper.insert:
insert into sys_perm (
id, pid, name, type, url, url_submit, path, leaf, level_num, sort_num, create_time, update_time,
) values
(
#{item.id}, #{item.pid}, #{item.name}, #{item.type}, #{item.url}, #{item.urlSubmit}, #{item.path}, #{item.leaf}, #{item.levelNum}, #{item.sortNum}, #{item.createTime}, #{item.updateTime},
)

insert ignore into sys_perm (
id, pid, name, type, url, url_submit, path, leaf, level_num, sort_num, create_time, update_time,
) values
(
#{item.id}, #{item.pid}, #{item.name}, #{item.type}, #{item.url}, #{item.urlSubmit}, #{item.path}, #{item.leaf}, #{item.levelNum}, #{item.sortNum}, #{item.createTime}, #{item.updateTime},
)

mapper.update:
update sys_perm set


            ,id = #{id}


            ,pid = #{pid}


            ,name = #{name}


            ,type = #{type}


            ,url = #{url}


            ,url_submit = #{urlSubmit}


            ,path = #{path}


            ,leaf = #{leaf}


            ,level_num = #{levelNum}


            ,sort_num = #{sortNum}


            ,create_time = #{createTime}


            ,update_time = #{updateTime}


where



                and id in

                    #{item}




                    and id = #{
                    updateWhereParams.id}





