mapper.insert:
insert into sys_role (
id, name, create_time, update_time,
) values
(
#{item.id}, #{item.name}, #{item.createTime}, #{item.updateTime},
)

insert ignore into sys_role (
id, name, create_time, update_time,
) values
(
#{item.id}, #{item.name}, #{item.createTime}, #{item.updateTime},
)

mapper.update:
update sys_role set


            ,id = #{id}


            ,name = #{name}


            ,create_time = #{createTime}


            ,update_time = #{updateTime}


where



                and id in

                    #{item}




                    and id = #{
                    updateWhereParams.id}





                and name in

                    #{item}




                    and name = #{
                    updateWhereParams.name}





                and create_time in

                    #{item}




                    and create_time = #{
                    updateWhereParams.createTime}





                and update_time in

                    #{item}




                    and update_time = #{
                    updateWhereParams.updateTime}

update sys_role set
id = #{id}, name = #{name}, create_time = #{createTime}, update_time = #{updateTime},
where id = #{id}

