mapper.insert:
insert into sys_sample (
id, name, weight, info, version, create_time,
) values
(
#{item.id}, #{item.name}, #{item.weight}, #{item.info}, #{item.version}, #{item.createTime},
)

insert ignore into sys_sample (
id, name, weight, info, version, create_time,
) values
(
#{item.id}, #{item.name}, #{item.weight}, #{item.info}, #{item.version}, #{item.createTime},
)

mapper.update:
update sys_sample set


            ,id = #{id}


            ,name = #{name}


            ,weight = #{weight}


            ,info = #{info}


            ,version = #{version}


            ,create_time = #{createTime}


where



                and id in

                    #{item}




                    and id = #{
                    updateWhereParams.id}





                and name in

                    #{item}




                    and name = #{
                    updateWhereParams.name}





                and weight in

                    #{item}




                    and weight = #{
                    updateWhereParams.weight}





                and info in

                    #{item}




                    and info = #{
                    updateWhereParams.info}





                and version in

                    #{item}




                    and version = #{
                    updateWhereParams.version}





                and create_time in

                    #{item}




                    and create_time = #{
                    updateWhereParams.createTime}

update sys_sample set
id = #{id}, name = #{name}, weight = #{weight}, info = #{info}, version = #{version}, create_time = #{createTime},
where id = #{id}

