mapper.insert:
insert into sys_user (
id, username, password, salt, disabled, create_time, update_time,
) values
(
#{item.id}, #{item.username}, #{item.password}, #{item.salt}, #{item.disabled}, #{item.createTime}, #{item.updateTime},
)

insert ignore into sys_user (
id, username, password, salt, disabled, create_time, update_time,
) values
(
#{item.id}, #{item.username}, #{item.password}, #{item.salt}, #{item.disabled}, #{item.createTime}, #{item.updateTime},
)

mapper.update:
update sys_user set


            ,id = #{id}


            ,username = #{username}


            ,password = #{password}


            ,salt = #{salt}


            ,disabled = #{disabled}


            ,create_time = #{createTime}


            ,update_time = #{updateTime}


where



                and id in

                    #{item}




                    and id = #{
                    updateWhereParams.id}





                and username in

                    #{item}




                    and username = #{
                    updateWhereParams.username}





                and password in

                    #{item}




                    and password = #{
                    updateWhereParams.password}





                and salt in

                    #{item}




                    and salt = #{
                    updateWhereParams.salt}





                and disabled in

                    #{item}




                    and disabled = #{
                    updateWhereParams.disabled}





                and create_time in

                    #{item}




                    and create_time = #{
                    updateWhereParams.createTime}





                and update_time in

                    #{item}




                    and update_time = #{
                    updateWhereParams.updateTime}

update sys_user set
id = #{id}, username = #{username}, password = #{password}, salt = #{salt}, disabled = #{disabled}, create_time = #{createTime}, update_time = #{updateTime},
where id = #{id}

